import React, { Component } from 'react';
import axios from 'axios';
import {URL} from '../../../hoc/config';
import Button from '../../widgets/Buttons/buttons';
import style from './videosList.module.css';
import VideosListTemplate from './videosListTemplate';

class VideosList extends Component {

    state = {
        teams:[],
        videos:[],
        start: this.props.start,
        amount: this.props.amount,
        end: this.props.start+this.props.amount
    }

    renderTitle = () => {
        return this.props.title?
            <h3><strong>NBA</strong> Videos</h3>
        : null
    }

    loadMore = () => {

    }

    renderButton = () => {
        return this.props.loadmore?
            <Button type="loadmore" cta="Load More Videos" loadMore = {()=>this.loadMore()}>

            </Button>
        : <Button type="linkTo" cta="More Videos" linkTo="/videos"/>
    }

    UNSAFE_componentWillMount(){
        this.request(this.state.start, this.state.end)
    }

    request = (start, end) => {
        if(this.state.teams.length<1){
            axios.get(`${URL}/teams`)
            .then(response => {
                this.setState({
                    teams: response.data
                })
            })
        }
        axios.get(`${URL}/videos?_start=${start}&_end=${end}`)
        .then( response => {
            this.setState({
                videos: [...this.state.videos, ...response.data]
            })
        })
    }

    renderVideos = () => {
        let template = null

        switch(this.props.type){
            case 'card':
                template = (
                    <VideosListTemplate data={this.state.videos} teams={this.state.teams}/>
                )
                break;
            default:
                template = null;
        }
        return template;
    }

    render(){
        return(
            <div className={style.videosList_wrapper}>
                { this.renderTitle() }
                Videos List
                { this.renderVideos() }
                { this.renderButton() }
            </div>
        )
    }
    
}

export default VideosList;
