import React from 'react';
import NewsSlider from '../widgets/NewsSlider/slider';
import NewsList from '../widgets/NewsList/newsList';

const Home = () => {

    let start = 3
    let amount = 3

    const loadmore = () => {
        return(
            <div>
                <NewsList type="card"
                loadmore={true}
                start={start+amount}
                amount={3}
                />
                {start = start+amount}
            </div>
        )
    }

    return(
    <div>
        <NewsSlider type="featured" 
                    start={0} 
                    amount={3}
                    settings={{
                        dots: false
                    }}/>

        <NewsList type="card"
                  loadmore={true}
                  start={start}
                  amount={amount}

        />
        <button onClick={()=>loadmore()}>Load More</button>
    </div>
    )
}

export default Home;
